public class InfLoopPrintNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 int i =1;//value is not increased in loop

         do {
                System.out.println("value of i="+ i);
          }while(i>10); // if you put i<10 it becomes inf loop

	}

}
